import React, { useState } from 'react'

// Importando Imagens
import Fran from '../../Assets/embaixadorafran.jpg'
import Jani from '../../Assets/embaixadorajanidemenezes.jpg'
import Renata from '../../Assets/embaixadorarenatabrasilia.jpg'
import Greice from '../../Assets/embaixadoragreice.jpg'

// Importando componentes
import BotaoPrimario from '../../Componentes/BotaoSecundario/BotaoSecundario'
import BotaoSecundario from '../../Componentes/BotaoSecundario/BotaoSecundario'
import Embaixadora from '../../Componentes/NossasEmbaixadoras/Embaixadora/Embaixadora'
import Menu from '../../Componentes/Menu/Menu'
import BannerInicial from '../../Componentes/BannerInicial/BannerInicial'
import Newsletter from '../../Componentes/Newsletter/Newsletter'
import Rodape from '../../Componentes/Rodape/Rodape'
import Popup from '../../Componentes/Popup/Popup'

// Importando Data
import Eventos from '../../Data/Eventos.json'

import './Embaixadoras.css'

const Embaixadoras = props => {

  const [embaixadoras, setEmbaixadoras]  = useState(
    {"id": 1},
    {"id": 2},
    {"id": 3},
    {"id": 4},
  )

  const [abrirPopup, setPopupState] = useState(false)

    const abrindoEFechandoPopup = () => {
      setPopupState((prevState) => {
            return {abrirPopup: !prevState.abrirPopup}
      })
    }

    let popupAberto

    if(abrirPopup) {
      popupAberto = 
      <Popup fecharPopup={() => setPopupState(false)}>
        {Eventos.map((detalheEvento, index) => {
          return (
            <div>
              <h1>{detalheEvento.nomeEvento}</h1>
              <p>{detalheEvento.idEmbaixadora}</p>
            </div>

          )
        })} 
      </Popup> 
    }


  return (
    <div className="embaixadoras">

      {popupAberto}

      <Menu />

      <BannerInicial titulo="Quem nos ajuda a fazer a diferença" banner="foraDaCaixa"/>

      <div className="container">

          <h2>Nossas Embaixadoras</h2>

          <div className="paginaEmbaixadoras">

            <Embaixadora imagemEmbaixadora={Greice} descricaoImagem="Imagem da embaixadora Greice" nomeEmbaixadora="Greice Maria" cidadeEmbaixadora="Rio do Sul - SC" funcoes={abrindoEFechandoPopup} refs="1"/>

            <Embaixadora imagemEmbaixadora={Fran} descricaoImagem="Imagem da embaixadora Greice" nomeEmbaixadora="Fran de Morais" cidadeEmbaixadora="Blumenau - SC" funcoes={abrindoEFechandoPopup} refs="2"/>

            <Embaixadora imagemEmbaixadora={Renata} descricaoImagem="Imagem da embaixadora Greice" nomeEmbaixadora="Renata" cidadeEmbaixadora="Brasília - DF" funcoes={abrindoEFechandoPopup} refs="3"/>

            <Embaixadora imagemEmbaixadora={Jani} descricaoImagem="Imagem da embaixadora Greice" nomeEmbaixadora="Jani de Menezes" cidadeEmbaixadora="Brasília - DF" funcoes={abrindoEFechandoPopup} refs="4"/>

          </div>

      </div>

      <Newsletter />
      <Rodape />

    </div>
  )
}

export default Embaixadoras
